import React, { useState, useRef } from 'react';

function CustomDesign() {
  const [selectedFile, setSelectedFile] = useState(null);
  const [previewUrl, setPreviewUrl] = useState(null);
  const [productType, setProductType] = useState('sticker');
  const [quantity, setQuantity] = useState(1);
  const fileInputRef = useRef(null);

  const handleFileChange = (event) => {
    const file = event.target.files[0];
    if (file) {
      if (file.type === 'image/jpeg' || file.type === 'image/png') {
        setSelectedFile(file);
        const reader = new FileReader();
        reader.onloadend = () => {
          setPreviewUrl(reader.result);
        };
        reader.readAsDataURL(file);
      } else {
        alert('Please upload a PNG or JPG file');
      }
    }
  };

  const handleAddToCart = () => {
    if (!selectedFile) {
      alert('Please upload an image first');
      return;
    }

    const customProduct = {
      id: `custom-${Date.now()}`,
      name: `Custom ${productType.charAt(0).toUpperCase() + productType.slice(1)}`,
      price: productType === 'sticker' ? 149 : 399,
      image: previewUrl,
      quantity: quantity,
      isCustom: true,
      originalFile: selectedFile
    };

    // TODO: Add to cart functionality
    console.log('Adding custom product to cart:', customProduct);
  };

  return (
    <div className="mt-16 mb-8">
      <h2 className="text-2xl font-bold text-white mb-6 drop-shadow-[0_0_8px_#3b82f6]">Create Your Custom Design</h2>
      <div className="bg-slate-900/80 border-2 border-electric rounded-2xl shadow-neon p-6 animate-pulseGlow relative overflow-hidden">
        {/* Animated border glow */}
        <div className="absolute inset-0 rounded-2xl pointer-events-none border-2 border-electric/40 blur-md opacity-60 animate-pulseGlow"></div>
        <div className="grid grid-cols-1 md:grid-cols-2 gap-8 relative z-10">
          {/* Left side - File upload and preview */}
          <div className="space-y-4">
            <div className="border-2 border-dashed border-cyan-600 rounded-lg p-6 text-center bg-slate-800/60">
              <input
                type="file"
                ref={fileInputRef}
                onChange={handleFileChange}
                accept=".png,.jpg,.jpeg"
                className="hidden"
              />
              {!previewUrl ? (
                <div
                  onClick={() => fileInputRef.current?.click()}
                  className="cursor-pointer"
                >
                  <div className="text-cyan-400 mb-2">
                    <svg className="mx-auto h-12 w-12" stroke="currentColor" fill="none" viewBox="0 0 48 48">
                      <path d="M28 8H12a4 4 0 00-4 4v20m32-12v8m0 0v8a4 4 0 01-4 4H12a4 4 0 01-4-4v-4m32-4l-3.172-3.172a4 4 0 00-5.656 0L28 28M8 32l9.172-9.172a4 4 0 015.656 0L28 28m0 0l4 4m4-24h8m-4-4v8m-12 4h.02" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" />
                    </svg>
                  </div>
                  <p className="text-sm text-cyan-200">
                    Click to upload your design
                  </p>
                  <p className="text-xs text-cyan-600 mt-1">
                    PNG or JPG up to 10MB
                  </p>
                </div>
              ) : (
                <div className="relative">
                  <img
                    src={previewUrl}
                    alt="Preview"
                    className="max-h-64 mx-auto rounded-lg border-2 border-cyan-600 shadow-neon"
                  />
                  <button
                    onClick={() => {
                      setSelectedFile(null);
                      setPreviewUrl(null);
                    }}
                    className="absolute top-2 right-2 bg-black/70 text-cyan-400 p-1 rounded-full hover:bg-cyan-700 hover:text-white transition-colors"
                  >
                    <svg className="h-4 w-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M6 18L18 6M6 6l12 12" />
                    </svg>
                  </button>
                </div>
              )}
            </div>
          </div>

          {/* Right side - Product options */}
          <div className="space-y-6">
            <div>
              <label className="block text-sm font-medium text-cyan-200 mb-2">
                Product Type
              </label>
              <div className="flex space-x-4">
                <button
                  onClick={() => setProductType('sticker')}
                  className={`flex-1 py-2 px-4 rounded-lg border-2 transition-all duration-200 font-semibold text-white ${
                    productType === 'sticker'
                      ? 'bg-gradient-to-r from-electric to-cyan border-electric shadow-neon'
                      : 'bg-slate-800 border-cyan-700 hover:bg-slate-700'
                  }`}
                >
                  Sticker
                </button>
                <button
                  onClick={() => setProductType('poster')}
                  className={`flex-1 py-2 px-4 rounded-lg border-2 transition-all duration-200 font-semibold text-white ${
                    productType === 'poster'
                      ? 'bg-gradient-to-r from-electric to-cyan border-electric shadow-neon'
                      : 'bg-slate-800 border-cyan-700 hover:bg-slate-700'
                  }`}
                >
                  Poster
                </button>
              </div>
            </div>

            <div>
              <label className="block text-sm font-medium text-cyan-200 mb-2">
                Quantity
              </label>
              <div className="flex items-center border-2 border-cyan-700 rounded-lg w-32 bg-slate-900/60">
                <button
                  onClick={() => setQuantity(Math.max(1, quantity - 1))}
                  className="px-3 py-2 text-cyan-400 hover:text-white hover:bg-slate-800 transition-colors duration-200"
                >
                  -
                </button>
                <span className="flex-1 text-center py-2 text-white">{quantity}</span>
                <button
                  onClick={() => setQuantity(quantity + 1)}
                  className="px-3 py-2 text-cyan-400 hover:text-white hover:bg-slate-800 transition-colors duration-200"
                >
                  +
                </button>
              </div>
            </div>

            <div className="pt-4">
              <button
                onClick={handleAddToCart}
                disabled={!selectedFile}
                className={`w-full py-3 px-4 rounded-lg text-white font-medium transition-all duration-300 shadow-neon-btn bg-gradient-to-r from-electric to-cyan hover:scale-105 focus:outline-none focus:ring-2 focus:ring-electric/60 ${
                  selectedFile ? '' : 'bg-slate-700 cursor-not-allowed opacity-60'
                }`}
              >
                Add to Cart
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default CustomDesign;
