import React from 'react';
import { ShoppingCartIcon } from '@heroicons/react/24/outline';
import { Link, useLocation } from 'react-router-dom';

function Navbar() {
  const location = useLocation();
  return (
    <nav className="fixed top-0 left-0 right-0 z-50 bg-black/60 backdrop-blur-md border-b border-slate-800/60 shadow-lg">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex justify-between h-16 items-center">
          <Link to="/" className="text-2xl font-extrabold text-electric drop-shadow-[0_0_8px_#3b82f6] animate-pulseGlow tracking-tight select-none">
            Stickitize
          </Link>
          <div className="flex items-center space-x-8">
            <Link
              to="/"
              className={`relative text-white font-medium transition-all duration-200 px-2 py-1 ${location.pathname === '/' ? 'text-electric' : ''} hover:text-electric hover:drop-shadow-[0_0_8px_#3b82f6] after:absolute after:left-0 after:bottom-0 after:w-0 hover:after:w-full after:h-0.5 after:bg-electric after:transition-all after:duration-300`}
            >
              Home
            </Link>
            <Link
              to="/cart"
              className={`relative text-white font-medium flex items-center transition-all duration-200 px-2 py-1 ${location.pathname === '/cart' ? 'text-electric' : ''} hover:text-electric hover:drop-shadow-[0_0_8px_#3b82f6] after:absolute after:left-0 after:bottom-0 after:w-0 hover:after:w-full after:h-0.5 after:bg-electric after:transition-all after:duration-300`}
            >
              <ShoppingCartIcon className="h-6 w-6 mr-1" />
              Cart
            </Link>
            <a
              href="/orders"
              className="relative text-white font-medium transition-all duration-200 px-2 py-1 hover:text-electric hover:drop-shadow-[0_0_8px_#3b82f6] after:absolute after:left-0 after:bottom-0 after:w-0 hover:after:w-full after:h-0.5 after:bg-electric after:transition-all after:duration-300"
            >
              Order History
            </a>
          </div>
        </div>
      </div>
    </nav>
  );
}

export default Navbar;
