import React, { useState, useMemo } from 'react';
import { products } from '../data/products';

function ProductCard({ product }) {
  const [quantity, setQuantity] = useState(1);
  const [isHovered, setIsHovered] = useState(false);

  return (
    <div
      className={`group bg-slate-800/60 border border-slate-700 rounded-2xl shadow-md hover:shadow-neon transition-all duration-300 overflow-hidden relative hover:border-electric ${isHovered ? 'scale-105' : ''}`}
      onMouseEnter={() => setIsHovered(true)}
      onMouseLeave={() => setIsHovered(false)}
    >
      <div className="relative overflow-hidden">
        <img
          src={product.image}
          alt={product.name}
          className="w-full h-48 object-cover group-hover:scale-110 transition-transform duration-500"
        />
        <div className="absolute inset-0 bg-gradient-to-t from-black/60 to-transparent opacity-0 group-hover:opacity-100 transition-opacity duration-300"></div>
      </div>
      <div className="p-4">
        <h3 className="text-lg font-semibold text-white mb-2 group-hover:text-electric transition-colors duration-200">
          {product.name}
        </h3>
        <p className="text-electric font-bold mb-2">â‚¹{product.price}</p>
        <div className="flex flex-wrap gap-2 mb-3">
          {product.tags.map((tag, index) => (
            <span
              key={index}
              className="px-3 py-1 text-xs rounded-full border border-electric/40 text-cyan-100 bg-slate-900/60 font-medium tracking-wide shadow-sm hover:bg-electric/10 transition-colors duration-200"
            >
              {tag}
            </span>
          ))}
        </div>
        <div className="flex items-center justify-between">
          <div className="flex items-center border border-slate-700 rounded-full overflow-hidden bg-slate-900/60">
            <button
              onClick={() => setQuantity(Math.max(1, quantity - 1))}
              className="px-3 py-1 text-cyan-200 hover:text-white hover:bg-slate-800 transition-colors duration-200"
            >
              -
            </button>
            <span className="px-3 py-1 text-white">{quantity}</span>
            <button
              onClick={() => setQuantity(quantity + 1)}
              className="px-3 py-1 text-cyan-200 hover:text-white hover:bg-slate-800 transition-colors duration-200"
            >
              +
            </button>
          </div>
          <button
            className={`px-4 py-2 rounded-full font-medium transition-all duration-300 transform hover:scale-110 bg-gradient-to-r from-electric to-cyan text-white shadow-neon-btn border-0 focus:outline-none focus:ring-2 focus:ring-electric/60 ${isHovered ? 'ring-2 ring-electric/60' : ''}`}
          >
            Add to Cart
          </button>
        </div>
      </div>
    </div>
  );
}

function ProductGrid({ searchQuery }) {
  const filteredProducts = useMemo(() => {
    if (!searchQuery) return products;
    const query = searchQuery.toLowerCase();
    return products.filter(product => {
      const nameMatch = product.name.toLowerCase().includes(query);
      const tagMatch = product.tags.some(tag =>
        tag.toLowerCase().includes(query)
      );
      return nameMatch || tagMatch;
    });
  }, [searchQuery]);

  if (filteredProducts.length === 0) {
    return (
      <div className="text-center py-12">
        <h3 className="text-xl font-semibold text-cyan-100 mb-2">No results found</h3>
        <p className="text-slate-400">Try adjusting your search terms or browse our full collection</p>
      </div>
    );
  }

  return (
    <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6">
      {filteredProducts.map((product) => (
        <ProductCard key={product.id} product={product} />
      ))}
    </div>
  );
}

export default ProductGrid;
