import React from 'react';
import { MagnifyingGlassIcon } from '@heroicons/react/24/outline';

function SearchBar({ onSearch }) {
  return (
    <div className="max-w-2xl mx-auto mb-8">
      <div className="relative group">
        <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
          <MagnifyingGlassIcon className="h-5 w-5 text-gray-400 group-focus-within:text-primary transition-colors duration-200" />
        </div>
        <input
          type="text"
          className="block w-full pl-10 pr-3 py-3 bg-white/50 backdrop-blur-sm border border-gray-200 rounded-full leading-5 placeholder-gray-500 focus:outline-none focus:ring-2 focus:ring-primary focus:border-transparent transition-all duration-200 shadow-sm hover:shadow-md"
          placeholder="Search for stickers, posters, or themes..."
          onChange={(e) => onSearch(e.target.value)}
        />
        <div className="absolute inset-0 rounded-full bg-gradient-to-r from-primary/20 via-secondary/20 to-accent/20 opacity-0 group-hover:opacity-100 transition-opacity duration-200 -z-10 blur-xl"></div>
      </div>
    </div>
  );
}

export default SearchBar;
