import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { ShoppingCartIcon } from '@heroicons/react/24/outline';

const navLinks = [
  { to: '/', label: 'Home' },
  { to: '/stickers', label: 'Stickers' },
  { to: '/posters', label: 'Posters' },
  { to: '/cart', label: 'Cart' },
  { to: '/order-history', label: 'Order History' },
  { to: '/custom-design', label: 'Custom Design' },
  { to: '/admin-login', label: 'Admin Login' },
];

function Navbar() {
  const [open, setOpen] = useState(false);

  return (
    <nav className="sticky top-0 z-50 bg-slate-900/80 backdrop-blur-md border-b border-slate-800">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex items-center justify-between h-16">
          {/* Logo */}
          <Link to="/" className="text-2xl font-bold text-electric tracking-tight select-none">Stickitize</Link>

          {/* Desktop Nav */}
          <div className="hidden md:flex gap-6 items-center">
            {navLinks.map(link => (
              <Link
                key={link.to}
                to={link.to}
                className="text-cyan-200 hover:text-electric transition-colors font-medium px-2 py-1 rounded"
                onClick={() => setOpen(false)}
              >
                {link.label}
              </Link>
            ))}
          </div>

          {/* Hamburger Icon */}
          <button
            className="md:hidden p-2 rounded text-cyan-200 hover:bg-slate-800 focus:outline-none"
            onClick={() => setOpen(o => !o)}
            aria-label="Toggle menu"
          >
            {open ? <XMarkIcon className="w-7 h-7" /> : <Bars3Icon className="w-7 h-7" />}
          </button>
        </div>
      </div>
      {/* Mobile Menu */}
      {open && (
        <div className="fixed inset-0 z-50 flex items-center justify-center bg-black/60 backdrop-blur-sm">
          <div className="bg-slate-900 rounded-xl shadow-2xl p-8 w-full max-w-lg mx-4 relative animate-fadeInUp">
            <button
              className="absolute top-4 right-4 text-cyan-200 hover:text-electric text-2xl font-bold"
              onClick={() => setOpen(false)}
              aria-label="Close"
            >Ã—</button>
            <nav className="flex flex-col gap-4">
              {navLinks.map(link => (
                <Link
                  key={link.to}
                  to={link.to}
                  className="text-white hover:text-electric transition-colors"
                  onClick={() => setOpen(false)}
                >
                  {link.label}
                </Link>
              ))}
            </nav>
          </div>
        </div>
      )}
    </nav>
  );
}

export default Navbar;
