import React, { useState, useMemo } from 'react';
import { products } from '../data/products';

function ProductCard({ product }) {
  const [quantity, setQuantity] = useState(1);
  const [isHovered, setIsHovered] = useState(false);

  return (
    <div 
      className="group bg-white/50 backdrop-blur-sm rounded-2xl shadow-sm hover:shadow-glow transition-all duration-300 overflow-hidden"
      onMouseEnter={() => setIsHovered(true)}
      onMouseLeave={() => setIsHovered(false)}
    >
      <div className="relative overflow-hidden">
        <img
          src={product.image}
          alt={product.name}
          className="w-full h-48 object-cover transform group-hover:scale-105 transition-transform duration-500"
        />
        <div className="absolute inset-0 bg-gradient-to-t from-black/50 to-transparent opacity-0 group-hover:opacity-100 transition-opacity duration-300"></div>
      </div>
      <div className="p-4">
        <h3 className="text-lg font-semibold text-gray-800 mb-2 group-hover:text-primary transition-colors duration-200">
          {product.name}
        </h3>
        <p className="text-primary font-bold mb-2">â‚¹{product.price}</p>
        <div className="flex flex-wrap gap-2 mb-3">
          {product.tags.map((tag, index) => (
            <span
              key={index}
              className="px-2 py-1 text-xs bg-gradient-to-r from-primary/10 to-secondary/10 text-primary rounded-full border border-primary/20"
            >
              {tag}
            </span>
          ))}
        </div>
        <div className="flex items-center justify-between">
          <div className="flex items-center border rounded-full overflow-hidden">
            <button
              onClick={() => setQuantity(Math.max(1, quantity - 1))}
              className="px-3 py-1 text-gray-600 hover:bg-gray-100 transition-colors duration-200"
            >
              -
            </button>
            <span className="px-3 py-1">{quantity}</span>
            <button
              onClick={() => setQuantity(quantity + 1)}
              className="px-3 py-1 text-gray-600 hover:bg-gray-100 transition-colors duration-200"
            >
              +
            </button>
          </div>
          <button 
            className={`px-4 py-2 rounded-full font-medium transition-all duration-300 transform hover:scale-105 ${
              isHovered 
                ? 'bg-gradient-to-r from-primary to-secondary text-white shadow-glow'
                : 'bg-white text-primary border border-primary hover:border-transparent'
            }`}
          >
            Add to Cart
          </button>
        </div>
      </div>
    </div>
  );
}

function ProductGrid({ searchQuery }) {
  const filteredProducts = useMemo(() => {
    if (!searchQuery) return products;
    
    const query = searchQuery.toLowerCase();
    return products.filter(product => {
      const nameMatch = product.name.toLowerCase().includes(query);
      const tagMatch = product.tags.some(tag => 
        tag.toLowerCase().includes(query)
      );
      return nameMatch || tagMatch;
    });
  }, [searchQuery]);

  if (filteredProducts.length === 0) {
    return (
      <div className="text-center py-12">
        <h3 className="text-xl font-semibold text-gray-600 mb-2">No results found</h3>
        <p className="text-gray-500">Try adjusting your search terms or browse our full collection</p>
      </div>
    );
  }

  return (
    <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6">
      {filteredProducts.map((product) => (
        <ProductCard key={product.id} product={product} />
      ))}
    </div>
  );
}

export default ProductGrid;
