import React, { useState, useEffect } from 'react';
import { Link, useLocation } from 'react-router-dom';
import { ShoppingCartIcon } from '@heroicons/react/24/outline';

const navLinks = [
  { to: '/', label: 'Home' },
  { to: '/stickers', label: 'Stickers' },
  { to: '/posters', label: 'Posters' },
  { to: '/order-history', label: 'Order History' },
  { to: '/custom-design', label: 'Custom Design' },
  { to: '/admin-login', label: 'Admin Login' },
];

function Navbar() {
  const [open, setOpen] = useState(false);
  const [cartCount, setCartCount] = useState(0);
  const location = useLocation();

  useEffect(() => {
    const stored = JSON.parse(localStorage.getItem('cart') || '[]');
    setCartCount(stored.length);
  }, [location]);

  // Prevent background scroll when menu is open
  useEffect(() => {
    if (open) {
      document.body.style.overflow = 'hidden';
    } else {
      document.body.style.overflow = '';
    }
    return () => { document.body.style.overflow = ''; };
  }, [open]);

  return (
    <nav className="fixed top-0 left-0 right-0 z-50 bg-slate-900/80 backdrop-blur-md border-b border-slate-800">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex items-center justify-between h-16">
          <Link to="/" className="text-2xl font-bold text-electric">Stickitize</Link>
          <div className="hidden md:flex items-center gap-8 justify-end flex-1">
            {navLinks.map(link => (
              <Link
                key={link.to}
                to={link.to}
                className="text-cyan-200 hover:text-electric transition-colors px-3 py-1 rounded text-base font-medium"
              >
                {link.label}
              </Link>
            ))}
            <Link to="/cart" className="relative ml-4">
              <ShoppingCartIcon className="w-6 h-6 text-cyan-200" />
              {cartCount > 0 && (
                <span className="absolute -top-2 -right-2 bg-electric text-white text-xs rounded-full w-5 h-5 flex items-center justify-center">
                  {cartCount}
                </span>
              )}
            </Link>
          </div>
          {/* Hamburger Icon */}
          <button
            className="md:hidden text-cyan-200 hover:text-electric transition-colors text-3xl px-2"
            onClick={() => setOpen(true)}
            aria-label="Open menu"
          >
            ☰
          </button>
          {/* Cart Icon (Mobile) */}
          <Link to="/cart" className="relative md:hidden ml-2">
            <ShoppingCartIcon className="w-6 h-6 text-cyan-200" />
            {cartCount > 0 && (
              <span className="absolute -top-2 -right-2 bg-electric text-white text-xs rounded-full w-5 h-5 flex items-center justify-center">
                {cartCount}
              </span>
            )}
          </Link>
        </div>
      </div>
      {/* Mobile Menu */}
      {open && (
        <>
          {/* Overlay */}
          <div className="fixed inset-0 z-40 bg-black/60 transition-opacity duration-300" onClick={() => setOpen(false)} />
          {/* Slide-in Panel */}
          <div className="fixed top-0 right-0 h-full w-4/5 max-w-xs z-50 bg-black shadow-2xl flex flex-col transition-transform duration-300 transform translate-x-0">
            <button
              className="absolute top-6 right-6 text-electric text-4xl font-bold hover:text-cyan-400 transition-colors"
              onClick={() => setOpen(false)}
              aria-label="Close menu"
            >
              ×
            </button>
            <nav className="flex flex-col items-end justify-center flex-1 gap-8 pr-8 pt-24">
              {navLinks.map(link => (
                <Link
                  key={link.to}
                  to={link.to}
                  className="text-white hover:text-electric transition-colors text-xl font-semibold px-2 py-1"
                  onClick={() => setOpen(false)}
                >
                  {link.label}
                </Link>
              ))}
            </nav>
          </div>
        </>
      )}
    </nav>
  );
}

export default Navbar;
