import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { ShoppingCartIcon } from '@heroicons/react/24/outline';

const navLinks = [
  { to: '/', label: 'Home' },
  { to: '/stickers', label: 'Stickers' },
  { to: '/posters', label: 'Posters' },
  { to: '/cart', label: 'Cart' },
  { to: '/order-history', label: 'Order History' },
  { to: '/custom-design', label: 'Custom Design' },
  { to: '/admin-login', label: 'Admin Login' },
];

function Navbar() {
  const [open, setOpen] = useState(false);
  const [cartCount, setCartCount] = useState(0);

  useEffect(() => {
    const stored = JSON.parse(localStorage.getItem('cart') || '[]');
    setCartCount(stored.length);
  }, []);

  return (
    <nav className="fixed top-0 left-0 right-0 z-50 bg-slate-900/80 backdrop-blur-md border-b border-slate-800">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex items-center justify-between h-16">
          <Link to="/" className="text-2xl font-bold text-electric">StickItize</Link>
          <div className="hidden md:flex items-center gap-8">
            {navLinks.map(link => (
              <Link
                key={link.to}
                to={link.to}
                className="text-cyan-200 hover:text-electric transition-colors"
              >
                {link.label}
              </Link>
            ))}
          </div>
          <div className="flex items-center gap-4">
            <Link to="/cart" className="relative">
              <ShoppingCartIcon className="w-6 h-6 text-cyan-200" />
              {cartCount > 0 && (
                <span className="absolute -top-2 -right-2 bg-electric text-white text-xs rounded-full w-5 h-5 flex items-center justify-center">
                  {cartCount}
                </span>
              )}
            </Link>
            <button
              className="md:hidden text-cyan-200 hover:text-electric transition-colors"
              onClick={() => setOpen(!open)}
              aria-label="Toggle menu"
            >
              {open ? '×' : '☰'}
            </button>
          </div>
        </div>
      </div>

      {/* Mobile Menu */}
      {open && (
        <div className="fixed inset-0 z-50 flex items-center justify-center bg-black/60 backdrop-blur-sm">
          <div className="bg-slate-900 rounded-xl shadow-2xl p-8 w-full max-w-lg mx-4 relative animate-fadeInUp">
            <button
              className="absolute top-4 right-4 text-cyan-200 hover:text-electric text-2xl font-bold"
              onClick={() => setOpen(false)}
              aria-label="Close"
            >×</button>
            <nav className="flex flex-col gap-4">
              {navLinks.map(link => (
                <Link
                  key={link.to}
                  to={link.to}
                  className="text-white hover:text-electric transition-colors"
                  onClick={() => setOpen(false)}
                >
                  {link.label}
                </Link>
              ))}
            </nav>
          </div>
        </div>
      )}
    </nav>
  );
}

export default Navbar;
