import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const deliveryLocations = [
  'Hostel A',
  'Hostel B',
  'Hostel C',
  'Main Gate',
  'Academic Block',
  'Sports Complex'
];

function Checkout() {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    fullName: '',
    mobile: '',
    email: '',
    deliveryMethod: 'pickup',
    deliveryLocation: '',
    paymentMethod: 'cod'
  });
  const [showPaymentModal, setShowPaymentModal] = useState(false);

  // Mock cart data - replace with actual cart state
  const cartItems = [
    {
      id: 1,
      name: "Minimalist Laptop Sticker",
      price: 99,
      quantity: 2,
      image: "https://images.unsplash.com/photo-1581091226825-a6a2a5aee158?w=500&auto=format&fit=crop&q=60"
    },
    {
      id: 2,
      name: "Custom Poster",
      price: 399,
      quantity: 1,
      image: "https://images.unsplash.com/photo-1518998053901-5348d3961a04?w=500&auto=format&fit=crop&q=60"
    }
  ];

  const subtotal = cartItems.reduce((total, item) => total + (item.price * item.quantity), 0);
  const deliveryFee = formData.deliveryMethod === 'delivery' ? 50 : 0;
  const total = subtotal + deliveryFee;

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (formData.paymentMethod === 'online') {
      setShowPaymentModal(true);
    } else {
      // Handle COD order
      console.log('Processing COD order:', formData);
      // TODO: Implement order processing
    }
  };

  const handlePaymentSuccess = () => {
    setShowPaymentModal(false);
    // TODO: Implement order success handling
    console.log('Payment successful, processing order:', formData);
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-navy via-slate-800 to-slate-900 py-8">
      <div className="max-w-4xl mx-auto px-4 sm:px-6 lg:px-8">
        <h1 className="text-3xl font-bold text-white mb-8">Checkout</h1>
        
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
          {/* Order Form */}
          <div className="bg-slate-900/80 border-2 border-electric rounded-xl p-6 shadow-neon">
            <form onSubmit={handleSubmit} className="space-y-6">
              <div>
                <label className="block text-cyan-200 text-sm font-medium mb-2">
                  Full Name *
                </label>
                <input
                  type="text"
                  name="fullName"
                  required
                  value={formData.fullName}
                  onChange={handleInputChange}
                  className="w-full px-4 py-2 bg-slate-800 border-2 border-cyan-700 rounded-lg text-white focus:border-electric focus:ring-2 focus:ring-electric/60 focus:outline-none transition-colors"
                />
              </div>

              <div>
                <label className="block text-cyan-200 text-sm font-medium mb-2">
                  Mobile Number *
                </label>
                <input
                  type="tel"
                  name="mobile"
                  required
                  pattern="[0-9]{10}"
                  value={formData.mobile}
                  onChange={handleInputChange}
                  className="w-full px-4 py-2 bg-slate-800 border-2 border-cyan-700 rounded-lg text-white focus:border-electric focus:ring-2 focus:ring-electric/60 focus:outline-none transition-colors"
                />
              </div>

              <div>
                <label className="block text-cyan-200 text-sm font-medium mb-2">
                  Email (optional)
                </label>
                <input
                  type="email"
                  name="email"
                  value={formData.email}
                  onChange={handleInputChange}
                  className="w-full px-4 py-2 bg-slate-800 border-2 border-cyan-700 rounded-lg text-white focus:border-electric focus:ring-2 focus:ring-electric/60 focus:outline-none transition-colors"
                />
              </div>

              <div>
                <label className="block text-cyan-200 text-sm font-medium mb-2">
                  Delivery Method *
                </label>
                <div className="flex space-x-4">
                  <label className="flex items-center">
                    <input
                      type="radio"
                      name="deliveryMethod"
                      value="pickup"
                      checked={formData.deliveryMethod === 'pickup'}
                      onChange={handleInputChange}
                      className="mr-2 text-electric focus:ring-electric"
                    />
                    <span className="text-white">Pickup</span>
                  </label>
                  <label className="flex items-center">
                    <input
                      type="radio"
                      name="deliveryMethod"
                      value="delivery"
                      checked={formData.deliveryMethod === 'delivery'}
                      onChange={handleInputChange}
                      className="mr-2 text-electric focus:ring-electric"
                    />
                    <span className="text-white">Delivery</span>
                  </label>
                </div>
              </div>

              {formData.deliveryMethod === 'delivery' && (
                <div>
                  <label className="block text-cyan-200 text-sm font-medium mb-2">
                    Delivery Location *
                  </label>
                  <select
                    name="deliveryLocation"
                    required
                    value={formData.deliveryLocation}
                    onChange={handleInputChange}
                    className="w-full px-4 py-2 bg-slate-800 border-2 border-cyan-700 rounded-lg text-white focus:border-electric focus:ring-2 focus:ring-electric/60 focus:outline-none transition-colors"
                  >
                    <option value="">Select a location</option>
                    {deliveryLocations.map(location => (
                      <option key={location} value={location}>
                        {location}
                      </option>
                    ))}
                  </select>
                </div>
              )}

              <div>
                <label className="block text-cyan-200 text-sm font-medium mb-2">
                  Payment Method *
                </label>
                <div className="flex space-x-4">
                  <label className="flex items-center">
                    <input
                      type="radio"
                      name="paymentMethod"
                      value="cod"
                      checked={formData.paymentMethod === 'cod'}
                      onChange={handleInputChange}
                      className="mr-2 text-electric focus:ring-electric"
                    />
                    <span className="text-white">Cash on Delivery</span>
                  </label>
                  <label className="flex items-center">
                    <input
                      type="radio"
                      name="paymentMethod"
                      value="online"
                      checked={formData.paymentMethod === 'online'}
                      onChange={handleInputChange}
                      className="mr-2 text-electric focus:ring-electric"
                    />
                    <span className="text-white">Online Payment</span>
                  </label>
                </div>
              </div>
            </form>
          </div>

          {/* Order Summary */}
          <div className="space-y-6">
            <div className="bg-slate-900/80 border-2 border-electric rounded-xl p-6 shadow-neon">
              <h2 className="text-xl font-semibold text-white mb-4">Order Summary</h2>
              <div className="space-y-4">
                {cartItems.map(item => (
                  <div key={item.id} className="flex items-center space-x-4">
                    <img
                      src={item.image}
                      alt={item.name}
                      className="w-16 h-16 rounded-lg object-cover border-2 border-cyan-700"
                    />
                    <div className="flex-1">
                      <h3 className="text-white font-medium">{item.name}</h3>
                      <p className="text-cyan-400">â‚¹{item.price} Ã— {item.quantity}</p>
                    </div>
                    <p className="text-electric font-semibold">â‚¹{item.price * item.quantity}</p>
                  </div>
                ))}
              </div>
              <div className="border-t border-cyan-700 mt-4 pt-4 space-y-2">
                <div className="flex justify-between text-cyan-200">
                  <span>Subtotal</span>
                  <span>â‚¹{subtotal}</span>
                </div>
                {deliveryFee > 0 && (
                  <div className="flex justify-between text-cyan-200">
                    <span>Delivery Fee</span>
                    <span>â‚¹{deliveryFee}</span>
                  </div>
                )}
                <div className="flex justify-between text-xl font-bold text-white">
                  <span>Total</span>
                  <span>â‚¹{total}</span>
                </div>
              </div>
            </div>

            <button
              onClick={handleSubmit}
              className="w-full py-4 bg-gradient-to-r from-electric to-cyan text-white rounded-full font-medium shadow-neon-btn hover:scale-105 hover:shadow-neon transition-all duration-300 text-lg"
            >
              Confirm Order
            </button>
          </div>
        </div>
      </div>

      {/* Payment Success Modal */}
      {showPaymentModal && (
        <div className="fixed inset-0 bg-black/60 backdrop-blur-sm flex items-center justify-center z-50">
          <div className="bg-slate-900 border-2 border-electric rounded-xl p-8 max-w-md w-full mx-4 shadow-neon animate-pulseGlow">
            <div className="text-center">
              <div className="text-6xl mb-4">ðŸ’³</div>
              <h3 className="text-2xl font-bold text-white mb-4">Payment Simulation</h3>
              <p className="text-cyan-200 mb-6">This is a demo payment flow. Click below to simulate successful payment.</p>
              <button
                onClick={handlePaymentSuccess}
                className="w-full py-3 bg-gradient-to-r from-electric to-cyan text-white rounded-full font-medium shadow-neon-btn hover:scale-105 hover:shadow-neon transition-all duration-300"
              >
                Simulate Payment Success
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default Checkout;
